dew = f.get('dew')
if (dew == 'good') return

light = f.get('light')
fan = f.get('fan')
aircon = f.get('aircon')
level = aircon + fan

if (light == '0') {
    if (dew == 'cold') {
        pl('00')
    } else {
        pl('10')
    }
} else {
    if (dew == 'cold') {
        switch (level) {
            case '00':
                return
            case '01':
                pl('00')
                break
            case '10':
                pl('01')
                break
            case '11':
                pl('10')
                break
        }
    } else {
        switch (level) {
            case '00':
                pl('01')
                break
            case '01':
                pl('10')
                break
            case '10':
                pl('11')
                break
            case '11':
                return
        }
    }
}

